import 'package:flutter/material.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      home: Scaffold(
        appBar: AppBar(title: const Text('Pecel Malang')),
        body: const PavlovaScreen(),
      ),
    );
  }
}

class PavlovaScreen extends StatelessWidget {
  const PavlovaScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return Row(
      children: [
        Expanded(
          flex: 2,
          child: Padding(
            padding: const EdgeInsets.all(16.0),
            child: Column(
              crossAxisAlignment: CrossAxisAlignment.start,
              children: [
                const Text(
                  'Pecel Malang',
                  style: TextStyle(
                    fontSize: 24,
                    fontWeight: FontWeight.bold,
                  ),
                ),
                const SizedBox(height: 8),
                const Text(
                  'Nasi pecel atau sega pecel adalah hidangan nasi Indonesia khas Jawa yang disajikan dengan pecel. Pecel dalam hidangan nasi pecel biasanya dibuat dari kangkung, kacang panjang, daun singkong, daun pepaya, atau bunga turi yang dicampur dengan sambal kacang'
                 ,
                  style: TextStyle(fontSize: 16),
                ),
                const SizedBox(height: 16),
                Row(
                  children: [
                    Icon(Icons.star, color: Colors.red[500]),
                    Icon(Icons.star, color: Colors.red[500]),
                    Icon(Icons.star, color: Colors.red[500]),
                    Icon(Icons.star, color: Colors.red[500]),
                    const Icon(Icons.star_half, color: Colors.red),
                    const SizedBox(width: 8),
                    const Text('170 Reviews'),
                  ],
                ),
                const SizedBox(height: 16),
                Row(
                  mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                  children: [
                    _buildIconInfo(Icons.timer, 'PREP:', '5 min'),
                    _buildIconInfo(Icons.schedule, 'COOK:', '10 min'),
                    _buildIconInfo(Icons.restaurant, 'FEEDS:', '4-6'),
                  ],
                ),
              ],
            ),
          ),
        ),
        Expanded(
          flex: 2,
          child: Image.network(
            'data:image/jpeg;base64,',
          ),
        ),
      ],
    );
  }

  Widget _buildIconInfo(IconData icon, String title, String subtitle) {
    return Column(
      children: [
        Icon(icon, color: Colors.green[500]),
        const SizedBox(height: 4),
        Text(title, style: const TextStyle(fontWeight: FontWeight.bold)),
        Text(subtitle),
      ],
    );
  }
}